const Article = (props) => {

    const { urlToImage, title, description, source } = props.article;

    let image = '';


    if(urlToImage !== ''){
        image= <img src={urlToImage} alt={title} className="card-img-top"/>
    } else {
        image = <img src='https://static.coindesk.com/wp-content/uploads/2018/03/BTC-chart-1-860x430.jpg' alt='text' className="card-img-top"/>        
    }

    return (
        <div className="col-md-6 col-12 mb-4">
            <div className="card">
                <div className="card-image">
                    {image}
                </div>
                <div className="card-body">
                    <h3 className="card title">{title}</h3>
                    <p className="card-text">{description}</p>
                    <p className="card-text">{source.name}</p>
                </div>
            </div>
        </div>
    )
}

export default Article;
