const Article = (props) => {

    const { urlToImage, url, title, description, source } = props.news;
    let image = '';
    if(urlToImage !== ''){
        image= <img src={urlToImage} alt={title} className="card-image-top"/>
    }

    return (
        <div className="col-md-6 col-12 mb-4">
            <div className="card">
                <div className="card-image">
                    {image}
                </div>
            </div>
        </div>
    )
}

export default Article;
