const Article = (props) => {

    const { urlToImage, url, title, description, source } = props.article;

    let image = '';

    if(urlToImage !== ''){
        image= <img src={urlToImage} alt={title} className="card-img-top"/>
    } else {
        image = null;
    }

    return (
        <div className="col-md-6 col-12 mb-4">
            <div className="card">
                <div className="card-image">
                    {image}
                </div>
            </div>
        </div>
    )
}

export default Article;
